import React, {Component} from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import {Paper} from '@material-ui/core';
import './Login.scss';
import {connect} from 'react-redux';

class Login extends Component {
	constructor(props) {
		super(props);
		this.state = {
			pseudo: "",
			password: ""
		}
	}
	OnSubmitLogin = (e) => {
		e.preventDefault();
		const stock = this.props.states.clients.filter(h => {
			return h.pseudo === e.target.pseudo.value && h.password === e.target.password.value;
		});

		const bool = e.target.pseudo.value && e.target.password.value && (stock.length > 0)
		if (bool) {
			this.props.history.push('/Task');
			console.log(stock);
		}
	}

	OnChangeLoginInput(e){
		this.setState({[e.target.name]: e.target.value});
	}

	render() {
		return (<div>
			<Paper className="PaperLogin">
				<form noValidate="noValidate" autoComplete="off" onSubmit={this.OnSubmitLogin.bind(this)}>
					<TextField label="PSEUDO" placeholder="Example" margin="normal" variant="outlined" value={this.state.pseudo} onChange={this.OnChangeLoginInput.bind(this)} name="pseudo"/>
					<br/>
					<TextField label="PASSWORD" type="password" autoComplete="current-password" margin="normal" variant="outlined" value={this.state.password} onChange={this.OnChangeLoginInput.bind(this)} name="password"/>
					<br/><br/>
					<Button type="submit" variant="contained">
						Connecter
					</Button>
				</form>
			</Paper>
		</div>);
	}
}

const mapStateToProps = state => {
	return {states: state.clients}
}

const mapDispatchToProps = dispatch => {
	return {
		deleteTask: id => {
			dispatch({type: "DELETE_TASK", id: id});
		},
		saveNumCli: data => {
			dispatch({type: "SAVE_NUM_CLIENT", data:data})
		}
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
